CREATE TABLE tbl_category_product (category_id int(10) NOT NULL AUTO_INCREMENT, category_name varchar(255), category_desc varchar(255), category_status int(10), PRIMARY KEY (category_id)) engine=InnoDB;
CREATE TABLE tbl_brand (brand_id int(10) NOT NULL AUTO_INCREMENT, brand_name varchar(255), brand_desc varchar(255), brand_status int(10), PRIMARY KEY (brand_id)) engine=InnoDB;
CREATE TABLE tbl_product (product_id int(10) NOT NULL AUTO_INCREMENT, category_id int(10), brand_id int(10), product_desc varchar(255), product_content varchar(255), product_price varchar(255), product_image varchar(255), product_status int(10), product_name varchar(255), tbl_category_productcategory_id int(10) NOT NULL, tbl_order_detailsorder_details_id int(10) NOT NULL, PRIMARY KEY (product_id)) engine=InnoDB;
CREATE TABLE tbl_customers (customer_id int(10) NOT NULL AUTO_INCREMENT, customer_name varchar(255) NOT NULL, customer_password int(100), customer_email varchar(255), customer_phone varchar(255), PRIMARY KEY (customer_id)) engine=InnoDB;
CREATE TABLE tbl_order (order_id int(10) NOT NULL AUTO_INCREMENT, customer_id int(10) NOT NULL, shipping_id int(10) NOT NULL, payment_id int(10) NOT NULL, order_total varchar(255), order_status int(10), tbl_customerscustomer_id int(10) NOT NULL, tbl_paymentpayment_id int(10) NOT NULL, PRIMARY KEY (order_id)) engine=InnoDB;
CREATE TABLE tbl_order_details (order_details_id int(10) NOT NULL AUTO_INCREMENT, order_id int(10) NOT NULL, product_id int(10) NOT NULL, product_name varchar(255), product_price varchar(255), product_sales_quantyti int(10), tbl_orderorder_id int(10) NOT NULL, PRIMARY KEY (order_details_id)) engine=InnoDB;
CREATE TABLE tbl_payment (payment_id int(10) NOT NULL AUTO_INCREMENT, payment_method int(10), payment_status int(10), PRIMARY KEY (payment_id)) engine=InnoDB;
CREATE TABLE tbl_shipping (shipping_id int(10) NOT NULL AUTO_INCREMENT, shipping_name int(255) NOT NULL, shipping_address varchar(255), shipping_phone varchar(255), shipping_email int(10), shipping_notes varchar(255), tbl_orderorder_id int(10) NOT NULL, PRIMARY KEY (shipping_id)) engine=InnoDB;
CREATE TABLE tbl_brand_tbl_product (tbl_brandbrand_id int(10) NOT NULL, tbl_productproduct_id int(10) NOT NULL, PRIMARY KEY (tbl_brandbrand_id, tbl_productproduct_id)) engine=InnoDB;
CREATE TABLE tbl_phieugiamgia (maphieu varchar(10) NOT NULL, madm int(10), mact int(10), tieude varchar(255), tieudengan varchar(255), diemnoibat varchar(255), dieukien varchar(255), noidung varchar(255), thoigianbatdau date, thoigianketthuc date, ngayhethan date, soluongnhonhat int(10), soluongmotuser int(10), soluongdamua int(10), giagoc float, giaban float, hoahong int(10), tinhtrang tinyint(1), sendAds tinyint(1), PRIMARY KEY (maphieu)) engine=InnoDB;
CREATE TABLE tbl_khuvuc (khuvuc_id int(10) NOT NULL AUTO_INCREMENT, khuvuc_name varchar(255), khuvuc_status int(10), PRIMARY KEY (khuvuc_id)) engine=InnoDB;
CREATE TABLE tbl_khuvuc_tbl_phieugiamgia (tbl_khuvuckhuvuc_id int(10) NOT NULL, tbl_phieugiamgiamaphieu varchar(10) NOT NULL, tinhtrang int(10), PRIMARY KEY (tbl_khuvuckhuvuc_id, tbl_phieugiamgiamaphieu)) engine=InnoDB;
CREATE TABLE tbl_thaoluan (thaoluan_id int(10), thaoluan_email varchar(255), thaoluan_desc varchar(255), thaoluan_status int(10), tbl_phieugiamgiamaphieu varchar(10) NOT NULL, tbl_productproduct_id int(10) NOT NULL) engine=InnoDB;
